			*/
			if(randomAttack==3){
				//jump StateFiendJump
				console.log( " fiend x  Time  hero   x =  "+this.X  );
				this.State = new StateFiendJump(1,currentFrameTime,this.timeFrame,this.CANVAS_WIDTH,this.Speed,this.HeroModel,this.ZONA_ATTACK, this.X);
			}
		}
	};
	GetState = function(){
		return this.State.GetState();
	};
	GetAttackPlayer = function(){
		return this.State.GetAttackPlayer();
	}
