}
class StateFiendBasic {
	State = 10;
	timeFrame;
	AttackFiend=false;
	AttackPlayer=false;
	AttackFiendTime;
	SetState = function(State) {
		this.State = State;
	};
	GetState = function() {
		return this.State;
	};
	GetAttackFiend= function() {
		return this.AttackFiend;
	};
	GetAttackPlayer = function() {
		return this.AttackPlayer;
	};
