class StateFiendMove extends StateFiendBasic {
  constructor(LocationLineReserve, AttackFiendTime, timeFrame, CANVAS_WIDTH, Speed, HeroModel, ZONA_ATTACK, Xfiend, MoveFix, XfixMove, _Y2) {
