          this.SetState(1);
          return new StateParam(false, X, Y, false, false);
        }

        if (proc > .8 && proc < .9) {
          this.SetState(2);
          return new StateParam(false, X, Y, false, false);
        }

        if (proc > .9) {
          this.SetState(3);

          if (this.MoveInAttackLine == true) {
