	constructor(Id,X,Y,ReserveY,timeFrame,CANVAS_WIDTH,ZONA_ATTACK,currentFrameTime,StartX,TableMonster){
		//TypeUnit,HPlife
		this.Id = Id;
		
		this.Ystatic =Y;
		this.Time =0;
		this.timeFrame = timeFrame;
		this.CANVAS_WIDTH = CANVAS_WIDTH;

		this.State = new StateFiendMoveFix(1,currentFrameTime,this.timeFrame,this.CANVAS_WIDTH,this.Speed,this.HeroModel,this.ZONA_ATTACK, this.X);
		this.X=StartX;
		this.Y=Y;
		this.ReserveY = ReserveY;
		this.ZONA_ATTACK = ZONA_ATTACK;
		this.TypeUnit = TableMonster.TypeUnit;
		this.HPlife = TableMonster.HPlife;
		this.Name = TableMonster.Name
	};
	GetId = function() {
		return this.Id;
	};
	UpdateRandomAttack = function(currentFrameTime,HeroModel) {
		this.HeroModel = HeroModel;

		if(this.State.GetAttackFiend()==false){
			var randomAttack = Math.floor(Math.random() * this.MaxAttack);
			if(randomAttack==1){
				this.Y = this.Ystatic;
				//	this.State =new StateFiend(1,false,true,currentFrameTime,this.timeFrame);
				this.State =new StateFiendAttack(1,false,true,currentFrameTime,this.timeFrame);
			}
			/*
			if(randomAttack==2){
				
				//move
				this.State = new StateFiendMove(1,currentFrameTime,this.timeFrame,this.CANVAS_WIDTH,this.Speed,this.HeroModel,this.ZONA_ATTACK, this.X);
				
			}
			*/
			if(randomAttack==3){
				//jump StateFiendJump
				
				this.State = new StateFiendJump(1,currentFrameTime,this.timeFrame,this.CANVAS_WIDTH,this.Speed,this.HeroModel,this.ZONA_ATTACK, this.X, this.Y);
			}
			
		}
		
	};
	GetTypeUnit = function(){
		return this.TypeUnit;
	}
	GetState = function(){
		return this.State.GetState();
	};
	GetAttackPlayer = function(){
		return this.State.GetAttackPlayer();
	}
	SetAttackPlayer = function(AttackPlayer,currentFrameTime){
		this.HPlife -=10;
		console.log( this.Name+  "  - DEAd - ==== " +this.HPlife);
		if (0>=this.HPlife){
			
		
			if (this.State.Name !="Dead"){
				console.log(  "_ === ZZZZZZ ZZZZ = Y = "  );
				this.State =new StateFiendDead(1,true,false,currentFrameTime,this.timeFrame,this.Ystatic);
				this.AttackPlayer = true;
			}
		} else {
			console.log(    " - not DEAd   MoveInAttackLine " );
											//State,AttackFiendTime,timeFrame,Speed
			this.State =new StateFiendBlow(1,currentFrameTime,this.timeFrame,1);
		}
	}
	GetStateTime = function(currentFrameTime) {
		var stateParam = this.State.Update(currentFrameTime,this.X,this.Y);
		if(stateParam.ChangeStatus){
			
			this.State =new StateFiendOld(1,false,false,currentFrameTime,this.timeFrame,this.Ystatic);
			//Update
		}
		this.X = stateParam.X;
		this.Y = stateParam.Y;
		if (stateParam.Dead ==true){
			this.Dead = true;
		}
		return this.State.GetState();
	
	};
}


// Move
class StateFiendMove extends StateFiendBasic {
	XstartJump;
	randomMoveX;
	moveY;
	lengthMove;
	Speed;
	ZONA_ATTACK;
	HeroModel;
	Jump = false;
	MoveFix = false;
	ReserveY;
	AttackY;
	MoveInAttackLine = false;
	constructor(State,AttackFiendTime,timeFrame,CANVAS_WIDTH,Speed,HeroModel,ZONA_ATTACK,Xfiend,MoveFix,XfixMove,Y){
		super();
		this.State =8;
		this.AttackFiendTime = AttackFiendTime;
		this.timeFrame = timeFrame;
		this.Speed = Speed;
		this.ZONA_ATTACK = ZONA_ATTACK;
		this.HeroModel = HeroModel;
		this.XstartJump = Xfiend;
		this.Name ="Move";
		this.ReserveY = new GameParam().LineReserve;
		this.AttackY = new GameParam().LineAttack;
		if(Y ==this.ReserveY){
			
			this.MoveInAttackLine = true;
		}
		
		
		
		if(MoveFix==true){
			this.MoveFix = true;
			this.randomMoveX =XfixMove;
		}
		// move fiend
		var random = false;
		
		if(this.MoveFix){
			
		} else {
			if (random){
				this.randomMoveX = Math.floor(Math.random() * CANVAS_WIDTH);
			} else {
				this.randomMoveX = this.HeroModel.X;
			}
		}
		
		this.lengthMove = this.randomMoveX - Xfiend;
		
	}
	Update = function(currentFrameTime,X,Y) {
		
		if (this.AttackFiendTime+(this.timeFrame*9)<currentFrameTime){
				this.SetState(1);
				return new StateParam(true,X,Y);
		}
		if (X!=this.randomMoveX){
			if(X>this.randomMoveX){
				X-=this.Speed;
				//11
				this.State =12;
			}
			if(X<this.randomMoveX){
				X+=this.Speed;
				//10
				this.State =11;
			}
		}
		if (this.Jump) {
			//jump  Math.abs   ReserveY
			var pathLength = (this.XstartJump - this.randomMoveX);
			var residual = Math.abs(this.XstartJump - X);
			var proc = Math.abs(residual/pathLength);
			
			if (proc<.5){
				Y =30-(50*proc);
			}
			if (proc>=.5){
				var procEnd =0.5-(proc-0.5);
				Y =30-(50*procEnd);
			}
			
			/*
			var setY = Math.sin(proc*10)*50+30;
			if(setY<=30){
				
				Y = setY;
			}
			*/
			if(proc<.7){
				this.SetState(0);
				return new StateParam(false,X,Y);
			}
			if(proc>.7&&proc<.8){
				this.SetState(1);
				return new StateParam(false,X,Y);
			}
			if(proc>.8&&proc<.9){
				this.SetState(2);
				return new StateParam(false,X,Y);
			}
			if(proc>.9){
				this.SetState(3);
				if (this.MoveInAttackLine == true){
					console.log( "  DEA Le =   MoveInAttackLine " );
					Y = this.AttackY;
				}
				
				return new StateParam(false,X,Y);
			}
	
		}
		if(this.MoveFix){
			if (new ZonaAttack().ZoneAttack(X,this.randomMoveX,this.ZONA_ATTACK)){
					//stop move
					
					return new StateParam(true,X,Y);
				}
		} else 
		{
			

				if (new ZonaAttack().ZoneAttack(X,this.HeroModel.X,this.ZONA_ATTACK)){
					//stop move
					
					return new StateParam(true,X,Y);
				}
		}
		return new StateParam(false,X,Y);
	}
}
