class TemplateFiend {
	MaxAttack = 10;
	StateImage=0;
	constructor(Id,X,Y,AttackPlayer){
		this.Id = Id;
		this.X=X;
		this.Y=Y;
		this.AttackPlayer=AttackPlayer;
		this.Time =0;
	};
	GetId = function() {
		return this.Id;
	};
	RandomAttack = function(currentFrameTime) {
		
		var randomAttack = Math.floor(Math.random() * this.MaxAttack);
		if(randomAttack==1){
			this.AttackFiend = true;
			this.AttackFiendTime = currentFrameTime;
			console.log(" a w ="  );
		}
	
	};
	GetState = function(currentFrameTime) {
		if (this.AttackFiend){
			if (this.AttackFiendTime+400<currentFrameTime){
				return 1;
			}
			if (this.AttackFiendTime+800<currentFrameTime){
				return 2;
			}
			if (this.AttackFiendTime+1200<currentFrameTime){
				return 3;
			}
			if (this.AttackFiendTime+1600<currentFrameTime){
				return 4;
			}
			if (this.AttackFiendTime+2000<currentFrameTime){
				return 5;
			}
			if (this.AttackFiendTime+2400<currentFrameTime){
				return 6;
			}
			if (this.AttackFiendTime+2800<currentFrameTime){
				return 7;
			}
			if (this.AttackFiendTime+3200<currentFrameTime){
				this.AttackFiend = false;
			}
			//1
			return 7;
		}
		
		if(this.AttackPlayer){
			//dead
			return 8;
		}
		return 0;
	};
}
class AnotherTemplate {}

export { TemplateFiend, AnotherTemplate }
