import { GameParam } from './GameParam';

class View {
	
	DrawHero = (ctx,ModelHero,CurrentFrameTime,_heroList,base_image) =>
	{
		var coefDraw = new GameParam().coefDraw;

		var countAnimInf = ModelHero.GetState(CurrentFrameTime)
		
		//console.log(   ModelHero.Nose+ " BLO I   = "+(700+ModelHero.Nose) );
		
		
		
		//1500
		//var moveHeroX = ModelHero.Nose*0.9;
		this.DrawCanvas(ctx,_heroList,base_image,countAnimInf,ModelHero,coefDraw);
		
		/*
		ctx.drawImage(base_image, 
				_heroList[countAnimInf].img[0].x,//x origin
				_heroList[countAnimInf].img[0].y,//y origin
				_heroList[countAnimInf].img[0].w,//w origin
				_heroList[countAnimInf].img[0].h,//h origin
				this.GetViewUnitX(ModelHero.Nose),
				//(300*coefDraw)+moveHeroX,
				//700+ModelHero.Nose,//*coefDraw,//x
				39*coefDraw,//y
				_heroList[countAnimInf].img[0].w*coefDraw,//w size
				_heroList[countAnimInf].img[0].h*coefDraw//h size
				);
		*/
		
		
		return;
	};
	DrawCanvas =(ctx,_heroList,base_image,countAnimInf,ModelHero,coefDraw) =>{
		
		
		
		ctx.drawImage(base_image, 
				_heroList[countAnimInf].img[0].x,//x origin
				_heroList[countAnimInf].img[0].y,//y origin
				_heroList[countAnimInf].img[0].w,//w origin
				_heroList[countAnimInf].img[0].h,//h origin
				this.GetViewUnitX(ModelHero.Nose),//x
				39*coefDraw,//y
				_heroList[countAnimInf].img[0].w*coefDraw,//w size
				_heroList[countAnimInf].img[0].h*coefDraw//h size
				);
		
	};
	DrawFiend = (ctx,currentFrameTime,ModelLevel,devilEye_image,_devilEyeList,_inventory_image,_inventoryList) =>
	{
		var coefDraw = new GameParam().coefDraw;
		
		//sort depth
		var reservLine_ar = [];
		var attackLine_ar = [];
		
		for(var i=0;i<ModelLevel.fiendModelList.length;i++){
			 
			if(ModelLevel.fiendModelList[i].GetLocationLineReserve()==true){
				reservLine_ar.push(ModelLevel.fiendModelList[i]);
			} else {
				attackLine_ar.push(ModelLevel.fiendModelList[i]);
			}
		}

		
		
		ModelLevel.fiendModelList = reservLine_ar.concat(attackLine_ar);
	
	
		for(var i=0;i<ModelLevel.fiendModelList.length;i++){
		
			var ModelFiend = ModelLevel.fiendModelList[i];
		
			var countAnimInf =ModelFiend.GetStateTime(currentFrameTime);
			
			var heightTypeEnemy =ModelFiend.GetTypeUnit();
			
			var renderImage =devilEye_image;
			var renderImageList = _devilEyeList;
			
			
			if (ModelLevel.fiendModelList[i].Name==ModelLevel.fiendModelList[i].NamePharmacy){
				renderImage = _inventory_image;
				renderImageList =_inventoryList;
				//countAnimInf =1;
				//var kol = ModelLevel.fiendModelList[i].GetState();
				
				
			}
			
			if (ModelLevel.fiendModelList[i].Name==ModelLevel.fiendModelList[i].Dealer){
				renderImage = _inventory_image;
				renderImageList =_inventoryList;
				
			}
			

//var moveFiendX = ModelFiend.X*0.9;
			//ctx.save();
			//ctx.scale(-1, 1);
			//ctx.translate(CANVAS_WIDTH, 0);
	//ctx.scale(-1, 1);
			
			if (ModelFiend.GetAdditionallyStateDraw()!=null){
				if (ModelFiend.GetAdditionallyStateDraw().length>0)
				{
					
					for(var z=0;z<ModelFiend.GetAdditionallyStateDraw().length;z++)
					{
						
						
						this.CanvasDrawFiend(ctx,renderImageList,renderImage,ModelFiend.GetAdditionallyStateDraw()[z].GetIdimage(),
						heightTypeEnemy,
						ModelFiend.GetAdditionallyStateDraw()[z].X,
						ModelFiend.GetAdditionallyStateDraw()[z].Y,
						coefDraw);
						
						if (ModelFiend.GetAdditionallyStateDraw()[z].IsText()) {
							ctx.font = "bold 24px verdana, sans-serif ";
							ctx.fillStyle = "#ff0000";
							ctx.fillText('FPS '+coefDraw+'= '+ModelFiend.GetAdditionallyStateDraw()[z].X,ModelFiend.GetAdditionallyStateDraw()[z].X*coefDraw,ModelFiend.GetAdditionallyStateDraw()[z].Y+40);
						}
					}
				}
			}
			
			
			this.CanvasDrawFiend(ctx,renderImageList,renderImage,countAnimInf,heightTypeEnemy,ModelFiend.X,ModelFiend.Y,coefDraw);
			

			//	ctx.restore();
			
		}
	};
	CanvasDrawFiend = function(ctx,renderImageList,renderImage,countAnimInf,heightTypeEnemy,X,Y,coefDraw)
	{
		ctx.drawImage(renderImage, 
					renderImageList[countAnimInf].img[0].x,//x origin
					renderImageList[countAnimInf].img[0].y+(151*heightTypeEnemy),//y origin
					renderImageList[countAnimInf].img[0].w,//w origin
					renderImageList[countAnimInf].img[0].h,//h origin
					new View().GetViewUnitX(X),
					//(300*coefDraw)+moveFiendX,
					//ModelFiend.X*coefDraw,//x
					Y*coefDraw,//y
					renderImageList[countAnimInf].img[0].wView*coefDraw,//w size
					renderImageList[countAnimInf].img[0].hView*coefDraw//h size
					);
		
	};
	GetViewUnitX = function(X){
		return (300*new GameParam().coefDraw)+(X*0.9);
	};
}
export { View }
