class TemplateFiend {
	MaxAttack = 20;
	StateImage=0;
	//AttackFiend =false;
	AttackFiendTime =1000;
	State;
	constructor(Id,X,Y,AttackPlayer,timeFrame){
		this.Id = Id;
		this.X=X;
		this.Y=Y;
		this.AttackPlayer=AttackPlayer;
		this.Time =0;
		this.timeFrame = timeFrame;
		this.State = new StateFiend(0,false,false);
	};
	GetId = function() {
		return this.Id;
	};
	RandomAttack = function(currentFrameTime) {
		
		if(this.State.GetAttackFiend()==false){
			var randomAttack = Math.floor(Math.random() * this.MaxAttack);
			if(randomAttack==1){
				//this.AttackFiend = true;
				this.AttackFiendTime = currentFrameTime;
				this.State =new StateFiend(1,false,true);
			}
		}
	};
	GetState = function(){
		return this.State.GetState();
	};
	GetAttackPlayer = function(){
		return this.AttackPlayer;
	}
	SetAttackPlayer = function(AttackPlayer){
		this.AttackPlayer = AttackPlayer;
	}
	GetStateTime = function(currentFrameTime) {
		//var timeFrame = 400;
		if (this.State.GetAttackFiend()){
			if (this.AttackFiendTime+(this.timeFrame)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*2)>currentFrameTime){
				//this.State =new StateFiend(1);
				this.State.SetState(1);
				return 1;
			}
			if (this.AttackFiendTime+(this.timeFrame*2)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*3)>currentFrameTime){
				//this.State =new StateFiend(2);
				this.State.SetState(2);
				return 2;
			}
			if (this.AttackFiendTime+(this.timeFrame*3)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*4)>currentFrameTime){
				//this.State =new StateFiend(3);
				this.State.SetState(3);
				return 3;
			}
			if (this.AttackFiendTime+(this.timeFrame*4)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*5)>currentFrameTime){
				//this.State =new StateFiend(4);
				this.State.SetState(4);
				return 4;
			}
			if (this.AttackFiendTime+(this.timeFrame*5)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*6)>currentFrameTime){
				//this.State =new StateFiend(5);
				this.State.SetState(5);
				return 5;
			}
			if (this.AttackFiendTime+(this.timeFrame*6)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*7)>currentFrameTime){
				//this.State =new StateFiend(6);
				this.State.SetState(6);
				return 6;
			}
			if (this.AttackFiendTime+(this.timeFrame*7)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*8)>currentFrameTime){
				//this.State =new StateFiend(7);
				this.State.SetState(7);
				return 7;
			}
			if (this.AttackFiendTime+(this.timeFrame*8)<currentFrameTime){
				//attack
				//this.AttackFiend = false;
				this.State =new StateFiend(1,false,false);
			}
			//this.State =new StateFiend(7);
			this.State.SetState(7);
			return 7;
		}
		
		if(this.AttackPlayer){
			//dead
			//this.State =new StateFiend(8);
			this.State.SetState(8);
			return 8;
		}
		//this.State =new StateFiend(0);
		this.State.SetState(0);
		return 0;
	};
}
