class HeroModel {
	RightArm=false;
	LeftArm=false;
	Nose=0;
	X=0;
	Y=0;
	Health = 100;
	TIME_FRAME = 400;
	AnimDamage = false;
	constructor(timeFrame){
		this.TIME_FRAME = timeFrame;		
		if (this.AnimDamage==false ){
			this.AnimDamage = true;
		}
	};
	MinusHealth= function(Minus, currentFrameTime){
		this.Health -=Minus;
	};
	GetState = function() {
		if(this.RightArm) {
			return 1;
		}
		if(this.LeftArm) {
			return 2;
		}
		return 0;
	}
}
export { HeroModel }
