	}
	SetAttackPlayer = function(AttackPlayer){
		this.State.SetAttackPlayer(AttackPlayer);
	}
	GetStateTime = function(currentFrameTime) {
		//var timeFrame = 400;
		var stateParam = this.State.Update(currentFrameTime,this.X,this.Y);
