class TemplateFiend {
	Id;
	X;
	Y;
	MaxAttack = 20;
	StateImage=0;
	State;
	timeFrame;
	CANVAS_WIDTH;
	Speed =1;
	HeroModel;
	constructor(Id,X,Y,timeFrame,CANVAS_WIDTH,HeroModel){
		this.Id = Id;
		this.X=X;
		this.Y=Y;
		this.Time =0;
		this.timeFrame = timeFrame;
		this.CANVAS_WIDTH = CANVAS_WIDTH;
		this.State = new StateFiend(0,false,false,null,timeFrame);
		this._modelHero = _modelHero;
	};
	GetId = function() {
		return this.Id;
	};
	RandomAttack = function(currentFrameTime) {
		
		if(this.State.GetAttackFiend()==false){
			var randomAttack = Math.floor(Math.random() * this.MaxAttack);
			if(randomAttack==1){
				//this.AttackFiend = true;
				//this.AttackFiendTime = currentFrameTime;
				
	
					this.State =new StateFiend(1,false,true,currentFrameTime,this.timeFrame);
				
			}
			if(randomAttack==2){
				
				this.State = new StateFiendMove(1,currentFrameTime,this.timeFrame,this.CANVAS_WIDTH,this.Speed,HeroModel);
				
			}
		}
	};
	GetState = function(){
		return this.State.GetState();
	};
	GetAttackPlayer = function(){
		return this.State.GetAttackPlayer();
	}
	SetAttackPlayer = function(AttackPlayer){
		this.State.SetAttackPlayer(AttackPlayer);
	}
	GetStateTime = function(currentFrameTime) {
		//var timeFrame = 400;
		var stateParam = this.State.Update(currentFrameTime,this.X,this.Y);
		if(stateParam.ChangeStatus){
			
			this.State =new StateFiend(1,false,false,currentFrameTime,this.timeFrame);
		}
		this.X = stateParam.X;
		this.Y = stateParam.Y;
		return this.State.GetState();
	
	};
}
class StateFiendBasic {
	State = 10;
	timeFrame;
	AttackFiend=false;
	AttackPlayer=false;
	AttackFiendTime;
	SetState = function(State) {
		this.State = State;
	};
	GetState = function() {
		return this.State;
	};
	GetAttackFiend= function() {
		return this.AttackFiend;
	};
	GetAttackPlayer = function() {
		return this.AttackPlayer;
	};
	SetAttackPlayer = function(AttackPlayer){
		this.AttackPlayer=AttackPlayer;
	}
	GetAttackFiendTime = function() {
		return this.AttackFiendTime;
	};
}
// Move
class StateFiendMove extends StateFiendBasic {
	randomMoveX;
	Speed;
	constructor(State,AttackFiendTime,timeFrame,CANVAS_WIDTH,Speed,HeroModel){
		super();
		this.State =8;
		this.AttackFiendTime = AttackFiendTime;
		this.timeFrame = timeFrame;
		this.Speed = Speed;
		console.log(   " ---- imDama Fr  ooo " );
		
		// move fiend
		this.randomMoveX = Math.floor(Math.random() * CANVAS_WIDTH);
		console.log( " = c  = "+this.randomMoveX);
	}
	Update = function(currentFrameTime,X,Y) {
		
		if (this.AttackFiendTime+(this.timeFrame*9)<currentFrameTime){
				this.SetState(1);
				return new StateParam(true,X,Y);
		}
		if (X!=this.randomMoveX){
			if(X>this.randomMoveX){
				X-=this.Speed;
				this.State =9;
			}
			if(X<this.randomMoveX){
				X+=this.Speed;
				
				this.State =8;
			}
		}
		
		
		return new StateParam(false,X,Y);
	}
}
// Attack
class StateFiend extends StateFiendBasic{
	//State = 0;
	
	
	constructor(State,AttackPlayer,AttackFiend,AttackFiendTime,timeFrame){
		super();
		
		this.State = State;
		this.AttackPlayer = AttackPlayer;
		this.AttackFiend = AttackFiend;
		this.AttackFiendTime = AttackFiendTime;
		this.timeFrame = timeFrame;
	};
	
	
	Update = function(currentFrameTime,X,Y) {
		if (this.AttackFiend){
			
			if (this.AttackFiendTime+(this.timeFrame*0)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*2)>currentFrameTime){
				this.SetState(1);
				return new StateParam(false,X,Y);
			}
			if (this.AttackFiendTime+(this.timeFrame*2)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*3)>currentFrameTime){
				this.SetState(2);
				return new StateParam(false,X,Y);
			}
			if (this.AttackFiendTime+(this.timeFrame*3)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*4)>currentFrameTime){
				this.SetState(3);
				return new StateParam(false,X,Y);
			}
			if (this.AttackFiendTime+(this.timeFrame*4)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*5)>currentFrameTime){
				this.SetState(4);
				return new StateParam(false,X,Y);
			}
			if (this.AttackFiendTime+(this.timeFrame*5)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*6)>currentFrameTime){
				this.SetState(5);
				return new StateParam(false,X,Y);
			}
			if (this.AttackFiendTime+(this.timeFrame*6)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*7)>currentFrameTime){
				this.SetState(6);
				return new StateParam(false,X,Y);
			}
			if (this.AttackFiendTime+(this.timeFrame*7)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*8)>currentFrameTime){
				this.SetState(7);
				return new StateParam(false,X,Y);
			}
			
			
			if (this.AttackFiendTime+(this.timeFrame*9)<currentFrameTime){
				//attack
				this.SetState(1);
				return new StateParam(true,X,Y);
			}
			return new StateParam(false,X,Y);
		}
		
		if(this.AttackPlayer){
			//dead
			this.State =10;
			return new StateParam(false,X,Y);
		}
		this.State=0;
		return new StateParam(false,X,Y);
	}
}
class StateParam{
	ChangeStatus;
	X;
	Y;
	constructor(ChangeStatus,X,Y){
		this.ChangeStatus = ChangeStatus;
		this.X=X;
		this.Y=Y;
	}

}


export { TemplateFiend, StateFiend }
