class StateFiend extends StateFiendBasic{
	//State = 0;
	AttackFiend=false;
	AttackPlayer=false;
	AttackFiendTime;
	
	constructor(State,AttackPlayer,AttackFiend,AttackFiendTime,timeFrame){
		super();
		this.State = State;
		this.AttackPlayer = AttackPlayer;
		this.AttackFiend = AttackFiend;
		this.AttackFiendTime = AttackFiendTime;
		this.timeFrame = timeFrame;
	};
	
