	};
	SetAttackPlayer = function(AttackPlayer){
		this.AttackPlayer=AttackPlayer;
	}
	GetAttackFiendTime = function() {
		return this.AttackFiendTime;
	};
	Update = function(currentFrameTime) {
		if (this.AttackFiend){
			
			if (this.AttackFiendTime+(this.timeFrame*0)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*2)>currentFrameTime){
				this.State =1;
				
