				this.State=3;
				return false;
			}
			if (this.AttackFiendTime+(this.timeFrame*4)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*5)>currentFrameTime){
				this.State=4;
				return false;
			}
			if (this.AttackFiendTime+(this.timeFrame*5)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*6)>currentFrameTime){
				this.State=5;
				return false;
			}
			if (this.AttackFiendTime+(this.timeFrame*6)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*7)>currentFrameTime){
				this.State=6;
				return false;
			}
			if (this.AttackFiendTime+(this.timeFrame*7)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*8)>currentFrameTime){
				this.State=7;
				return false;
			}
			
			
			if (this.AttackFiendTime+(this.timeFrame*8)<currentFrameTime){
				//attack
				//this.AttackFiend = false;
				this.State=1;
				//this.State =new StateFiend(1,false,false);
