class TemplateFiend {
	MaxAttack = 10;
	StateImage=0;
	constructor(Id,X,Y,AttackPlayer){
		this.Id = Id;
		this.X=X;
		this.Y=Y;
		this.AttackPlayer=AttackPlayer;
		this.Time =0;
	};
	GetId = function() {
		return this.Id;
	};
	RandomAttack = function(currentFrameTime) {
		
		var randomAttack = Math.floor(Math.random() * this.MaxAttack);
		if(randomAttack==1){
			this.AttackFiend = true;
			this.AttackFiendTime = currentFrameTime+700;
			console.log(" a w ="  );
		}
	
	};
	GetState = function(currentFrameTime) {
		if (this.AttackFiend){
			if (this.AttackFiendTime<currentFrameTime){
				this.AttackFiend = false;
			}
			//1
			return 2;
		}
		
		if(this.AttackPlayer){
			return 5;
		}
		return 0;
	};
}
class AnotherTemplate {}

export { TemplateFiend, AnotherTemplate }
