class PlayMusic{
	//audio;
	constructor(){
		console.log("==" );
		const audio = new Audio("/BossTime.mp3");
		document.addEventListener('DOMContentLoaded', function(){
			this.startAudio();
		});
		
	};
	
	startAudio= function()  {
		
		this.audio.play(10000);
		this.audio.volume = .4;
		
	};
}
export { PlayMusic }
