class DealerState extends StateFiendBasic {
	ZONA_ATTACK;
	constructor(X,Y,State,timeFrame,AttackFiendTime,ZONA_ATTACK){
		super();
		this.X = X;
		this.Y = Y;
		this.Name ="Dealer";
		this.State = State;
		this.AttackFiendTime = AttackFiendTime;
		this.timeFrame = timeFrame;
		var gameParam = new GameParam();
		this.ListAdditionallyStateDraw =
		[
			new ItemDrawSample(1,-600,10,20,true,"COST: 10",false,10,0),
			new ItemDrawSample(2,-300,10,20,true,"COST: "+gameParam.TableWeapon[0].Cost,false,gameParam.TableWeapon[0].Cost,gameParam.TableWeapon[0].Cost.ImageId),
			new ItemDrawSample(3,0,10,20,true,"COST: "+gameParam.TableWeapon[1].Cost,false,gameParam.TableWeapon[1].Cost,gameParam.TableWeapon[1].Cost.ImageId),
			new ItemDrawSample(4,300,10,20,true,"EXIT",true,0,0),
			new ItemDrawSample(1,-600+100,10+10,2,false,"",false,0,0),
			new ItemDrawSample(2,-300,10,22,false,"",false,0,gameParam.TableWeapon[0].Cost.ImageId),
			new ItemDrawSample(3,0,10,23,false,"",false,0,gameParam.TableWeapon[1].Cost.ImageId),
