									}
								}
							}
							
				}
				
				
			}
		}
		
		//Y += 2;
		
		if (this.AttackFiendTime+(this.timeFrame*0)<currentFrameTime&&this.AttackFiendTime+(this.timeFrame*2)>currentFrameTime){
