class HeroModel {
	RightArm=false;
	LeftArm=false;
	Nose=0;
	X=0;
	Y=0;
	Health = 100;
	TIME_FRAME = 400;
	AnimDamage = false;
	AnimDamageTime =1000;
	State = 0;
	constructor(timeFrame){
		this.TIME_FRAME = timeFrame;		
		if (this.AnimDamage==false ){
			this.AnimDamage = true;
		}
	};
	MinusHealth= function(Minus, currentFrameTime){
		this.Health -=Minus;
		if(this.AnimDamage==false){
			this.AnimDamage = true;
			this.AnimDamageTime = currentFrameTime;
			console.log(" atta "   );
		}
	};
	GetState = function(currentFrameTime) {
		if (this.AnimDamage){
			if (this.AnimDamageTime+(this.timeFrame)<currentFrameTime&&this.AnimDamageTime+(this.timeFrame*2)>currentFrameTime){
				this.State =3;
				return 3;
			} else {
				this.AnimDamage = false;
			}
		}
		if(this.RightArm) {
			this.State =1;
			return 1;
		}
		if(this.LeftArm) {
			this.State =2;
			return 2;
		}
		this.State =0;
		return 0;
	}
}
export { HeroModel }
