				resetFiendModel(ModelLevel.fiendModelList,currentFrameTime);
				ModelLevel.fiendModelList = resetNewLevel(ModelLevel,currentFrameTime);
				
			}
		}
		lastFrameTime = currentFrameTime;
	}
	const DrawBackground=(ctx,Id) =>{
		
		if(Id == 0){
			ctx.drawImage(throneRoom_image, 0, 189*0, 939, 189, 0, 0, 939*coefDraw, 189*coefDraw);
