import { GameParam } from './GameParam';
import { StateParam } from './StateParam';

class HeroModel {
	RightArm=false;
	LeftArm=false;
	Nose=0;
	X=0;
	Y=0;
	Health = 100;
	TIME_FRAME = 400;
	AnimDamage = false;
	AnimDamageTime =1000;
	State;
	constructor(timeFrame){
		this.TIME_FRAME = timeFrame;		
		if (this.AnimDamage==false ){
			this.AnimDamage = true;
		}
		this.State = new StateHero(0);
	};
	SetNose = function(Nose){
		this.Nose=Nose;
		this.X =Nose;
	}
	SetRightArm = function(rightArm){
		this.RightArm = rightArm;
	};
	SetLeftArm= function(leftArm){
		this.LeftArm = leftArm;
	};
	MinusHealth= function(Minus, currentFrameTime){
		this.Health -=Minus;
		if(this.AnimDamage==false){
			this.AnimDamage = true;
			this.AnimDamageTime = currentFrameTime;
			//console.log(" att = " +this.AnimDamageTime  );
		}
	};
	GetState = function(currentFrameTime) {
		if (this.AnimDamage){
			
			if (this.AnimDamageTime+(this.TIME_FRAME*0)<=currentFrameTime&&this.AnimDamageTime+(this.TIME_FRAME*2)>=currentFrameTime){
				//damage 4
				this.State = new StateHero(4).StatePose;
				return this.State.GetState(currentFrameTime).StatePose;
			} else {
				this.AnimDamage = false;
			}
		}
		if(this.RightArm) {
			//attack 1
			this.State = new AttackState(currentFrameTime).StatePose;
			return this.State.GetState(currentFrameTime).StatePose;
		}
		if(this.LeftArm) {
			//defence 3
			this.State = new StateHero(3).StatePose;
			return this.State.GetState(currentFrameTime).StatePose;
		}
		//this.State =0;
		this.State = new StateHero(0).StatePose;
		return this.State.GetState(currentFrameTime).StatePose;
	}
}
class StateHero{
	State=0;
	Name ="StateHero";
	constructor(State) {
		this.State = State;
	};
	GetState = function(){
		//return this.State;
		return new StateParam(true,null,null,null,null,this.State);
	}
}
class AttackState extends StateHero {
	timeFrame;
	startCurrentFrameTime;
	
	constructor(currentFrameTime) {
		super(1);
		this.startCurrentFrameTime = currentFrameTime;
		this.timeFrame = new GameParam().TIME_FRAME;
		this.Name = "AttackState";
	}
	GetState = function(currentFrameTime){
		console.log( "  DEA L v Jump " );
		if (this.startCurrentFrameTime+(this.timeFrame*0)<currentFrameTime&&this.startCurrentFrameTime+(this.timeFrame*2)>currentFrameTime){
				console.log( " == ZZZ  = "   );
				//return 2;
				return new StateParam(true,null,null,null,null,2);
		}
		console.log( "  d x  Y A  x =  "  );
		//return 1;
		return new StateParam(true,null,null,null,null,1);
	}
}


export { HeroModel }
