import logo from './logo.svg';
import './App.css';

import React, { useRef } from "react";
import "./App.css";
import * as tf from "@tensorflow/tfjs";
import * as posenet from "@tensorflow-models/posenet";
import Webcam from "react-webcam";
import { drawKeypoints, drawSkeleton } from "./utilities";

function App() {
	 const webcamRef = useRef(null);
  const canvasRef = useRef(null);

	 //  Load posenet
  const runPosenet = async () => {
    const net = await posenet.load({
      inputResolution: { width: 640, height: 480 },
      scale: 0.8,
    });
    //
    setInterval(() => {
      detect(net);
    }, 100);
  };

  const detect = async (net) => {
    if (
      typeof webcamRef.current !== "undefined" &&
      webcamRef.current !== null &&
      webcamRef.current.video.readyState === 4
    ) {
      // Get Video Properties
      const video = webcamRef.current.video;
      const videoWidth = webcamRef.current.video.videoWidth;
      const videoHeight = webcamRef.current.video.videoHeight;

      // Set video width
      webcamRef.current.video.width = videoWidth;
      webcamRef.current.video.height = videoHeight;

      // Make Detections
      const pose = await net.estimateSinglePose(video);

	 // console.log(pose.keypoints[0].part+"  x = "+pose.keypoints[0].position.x+"  y = "+pose.keypoints[0].position.y);
	  //console.log(pose.keypoints[7].part+"  x = "+pose.keypoints[7].position.x+"  y = "+pose.keypoints[7].position.y);
	  //console.log(pose.keypoints[8].part+"  x = "+pose.keypoints[8].position.x+"  y = "+pose.keypoints[8].position.y);

	   //console.log(pose.keypoints[7].part+"  score = "+pose.keypoints[7].score+"  score = "+pose.keypoints[8].score);


      //console.log(pose);

      drawCanvas(pose, video, videoWidth, videoHeight, canvasRef);
    }
  };

	const drawCanvas = (pose, video, videoWidth, videoHeight, canvas) => {
		const ctx = canvas.current.getContext("2d");
		canvas.current.width = videoWidth;
		canvas.current.height = videoHeight;

	   drawKeypoints(pose["keypoints"], 0.6, ctx);
	   drawSkeleton(pose["keypoints"], 0.7, ctx);

	   drawCanvasBattle();
  };

	runPosenet();

  	//////////




	const drawCanvasBattle = () =>{
		var context = document.getElementById("myCanvas");

const  base_image = new Image();
		  base_image.src = 'scriptCanvas/scriptCanvas.js';
		  base_image.onload = function(){

		}
		if (context!=null){
			console.log("draw ="+base_image);
			var ctx = context.getContext("2d");
			ctx.moveTo(0, 0);
			ctx.lineTo(200, 100);
			ctx.stroke();




			//if (base_image!=null){
				ctx.drawImage(base_image, 0, 0);
			//}
		}
	};
	//////////

  return (

   <div className="App">

   <canvas id="myCanvas" width="300" height="300"  style={{position: "absolute"}}></canvas>
   <script src="scriptCanvas/scriptCanvas.js"></script>
      <header className="App-header">

        <Webcam
          ref={webcamRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
            left: 0,
            right: 0,
            textAlign: "center",
            zindex: 9,
            width: 640,
            height: 480,
          }}
        />

        <canvas
          ref={canvasRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
            left: 0,
            right: 0,
            textAlign: "center",
            zindex: 9,
            width: 640,
            height: 480,
          }}
        />
      </header>
    </div>
  );



}

export default App;
