class HeroModel {
	RightArm=false;
	LeftArm=false;
	Nose=0;
	X=0;
	Y=0;
	Health = 100;
	TIME_FRAME = 400;
	AnimDamage = false;
	AnimDamageTime =1000;
	State;
	constructor(timeFrame){
		this.TIME_FRAME = timeFrame;		
		if (this.AnimDamage==false ){
			this.AnimDamage = true;
		}
		this.State = new StateHero(0);
	};
	SetNose = function(Nose){
		this.Nose=Nose;
		this.X =Nose;
	}
	SetRightArm = function(rightArm){
		this.RightArm = rightArm;
	};
	SetLeftArm= function(leftArm){
		this.LeftArm = leftArm;
	};
	MinusHealth= function(Minus, currentFrameTime){
		this.Health -=Minus;
		if(this.AnimDamage==false){
			this.AnimDamage = true;
			this.AnimDamageTime = currentFrameTime;
			//console.log(" att = " +this.AnimDamageTime  );
		}
	};
	GetState = function(currentFrameTime) {
		if (this.AnimDamage){
			
			if (this.AnimDamageTime+(this.TIME_FRAME*0)<=currentFrameTime&&this.AnimDamageTime+(this.TIME_FRAME*2)>=currentFrameTime){
				//damage 4
				//this.State =4;
				this.State = new StateHero(4);
				return this.State.GetState();
			} else {
				this.AnimDamage = false;
			}
		}
		if(this.RightArm) {
			//attack
			//this.State =1;
			this.State = new StateHero(1);
			return this.State.GetState();
		}
		if(this.LeftArm) {
			//defence 3
			//this.State =3;
			this.State = new StateHero(3);
			return this.State.GetState();
		}
		//this.State =0;
		this.State = new StateHero(0);
		return this.State.GetState();
	}
}
