class TemplateFiend {
	MaxAttack = 10;
	StateImage=0;
	constructor(Id,X,Y,AttackPlayer){
		this.Id = Id;
		this.X=X;
		this.Y=Y;
		this.AttackPlayer=AttackPlayer;
		this.Time =0;
	};
	GetId = function() {
		return this.Id;
	};
	RandomAttack = function(currentFrameTime) {
		
		var randomAttack = Math.floor(Math.random() * this.MaxAttack);
		if(randomAttack==1){
			this.AttackFiend = true;
			this.AttackFiendTime = currentFrameTime;
			console.log(" a w ="  );
		}
	
	};
	GetState = function(currentFrameTime) {
		var timeFrame = 400;
		if (this.AttackFiend){
			if (this.AttackFiendTime+(timeFrame)<currentFrameTime){
				return 1;
			}
			if (this.AttackFiendTime+(timeFrame*2)<currentFrameTime){
				return 2;
			}
			if (this.AttackFiendTime+(timeFrame*3)<currentFrameTime){
				return 3;
			}
			if (this.AttackFiendTime+(timeFrame*4)<currentFrameTime){
				return 4;
			}
			if (this.AttackFiendTime+(timeFrame*5)<currentFrameTime){
				return 5;
			}
			if (this.AttackFiendTime+(timeFrame*6)<currentFrameTime){
				return 6;
			}
			if (this.AttackFiendTime+(timeFrame*7)<currentFrameTime){
				return 7;
			}
			if (this.AttackFiendTime+(timeFrame*8)<currentFrameTime){
				this.AttackFiend = false;
			}
			//1
			return 7;
		}
		
		if(this.AttackPlayer){
			//dead
			return 8;
		}
		return 0;
	};
}
class AnotherTemplate {}

export { TemplateFiend, AnotherTemplate }
